CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);
ALTER TABLE users
alter COLUMN password DROP NOT NULL;
ALTER TABLE users
ADD COLUMN google_id TEXT NULL;
CREATE TABLE history_purchase (
    id SERIAL PRIMARY KEY,
    stripe_id TEXT UNIQUE NOT NULL,
    date_purchase INTEGER NOT NULL,
    user_id INTEGER REFERENCES users(id),
    status varchar(50) NOT NULL
);
CREATE TABLE history_product (
    id SERIAL PRIMARY KEY,
    product_name TEXT NOT NULL,
    product_quantity INTEGER NOT NULL,
    product_price INTEGER NOT NULL,
    product_id TEXT REFERENCES history_purchase (stripe_id),
    product_image TEXT NOT NULL
);
CREATE TABLE temp_token (id SERIAL PRIMARY KEY, token TEXT);
ALTER TABLE temp_token
ALTER COLUMN id DROP DEFAULT;
